  );
   
  
 CREATE TABLE IF NOT EXISTS "medical_college"(
    "id" INTEGER GENERATED BY DEFAULT AS IDENTITY,
   "name" VARCHAR(255),
   "state" VARCHAR (255),
   "course_id" INTEGER,
   "university_id" INTEGER,
   "created_by" VARCHAR(255) NOT NULL DEFAULT NULL,
	"created_time" TIMESTAMP NOT NULL DEFAULT NULL,
	"modified_by" VARCHAR(255) NULL DEFAULT NULL,
	"modified_time" TIMESTAMP NULL DEFAULT NULL,
	FOREIGN KEY (course_id)REFERENCES medical_course(id),
	FOREIGN KEY (university_id)REFERENCES medical_university(id),
	 PRIMARY KEY("id")
	 );
	   

